import React, { useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import "./App.css";
import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import NavBar from "./NavBar";
import { Route, Switch } from "react-router-dom";
import Menu from "./FoodMenu";
import MenuList from "./MenuList";
import Snack from "./FoodItem";
import Drink from "./Drink";
import ItemDetail from "./ItemDetail";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [snacks, setSnacks] = useState([]);
  const [drinks, setDrinks] = useState([]);
  useEffect(() => {
    async function getSnacksandDrinks() {
      let snacks = await SnackOrBoozeApi.getSnacks();
      let drinks = await SnackOrBoozeApi.getDrinks();
      setSnacks(snacks);
      setDrinks(drinks);
      setIsLoading(false);
    }
    getSnacksandDrinks();
  }, []);

  if (isLoading) {
    return <p>Loading &hellip;</p>;
  }

  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <main>
          <Switch>
            <Route exact path="/">
              <Home snacks={snacks} drinks={drinks} />
            </Route>
            <Route exact path="/snacks">
  <           MenuList items={snacks} title="Snacks" itemType="snacks" />
</Route>
<Route path="/snacks/:id">
  <ItemDetail items={snacks} cantFind="/snacks" />
</Route>
<Route exact path="/drinks">
  <MenuList items={drinks} title="Drinks" itemType="drinks" />
</Route>
<Route path="/drinks/:id">
  <ItemDetail items={drinks} cantFind="/drinks" />
</Route>

            <Route>
              <p>Hmmm. I can't seem to find what you want.</p>
            </Route>
          </Switch>
        </main>
      </BrowserRouter>
    </div>
  );
}

export default App;
